app.put("/api/country/:id", (req, res) => {
  const { id } = req.params;
  const { name } = req.body;

  pool.query(
    "update countries set country_name = $1 where country_id = $2",
    [name, id],
    (error, result) => {
      if (error) {
        throw error;
      }
      res.status(200).end("Data berhasil update");
    }
  );
});

